drop table if exists albums;
drop table if exists artists;
drop table if exists songs;
drop table if exists playlist;
create table albums (artist_id INT,album_id INT,title CHAR(255),yearOfRelease INT,img_path CHAR(255));
create table artists (artist_id INT PRIMARY KEY, name CHAR(255));
create table songs (artist_id INT, album_id INT, song_name VARCHAR(255), duration VARCHAR(255));
create table playlist (artist_id INT, album_id INT, song_name VARCHAR(255), duration VARCHAR(255));
